package com.pl.schedule.protocol.udp.cmd;import java.util.Arrays;import com.pl.schedule.protocol.udp.Cmd;import com.pl.schedule.protocol.udp.model.ServerPara;public class GetServerPara extends CommCmd {	public ServerPara getResult() {		return new ServerPara(Arrays.copyOfRange(this.recvBuff, 4, this.recvBuff.length));	}	public GetServerPara(String ip, byte[] sn) {		super(ip, sn);		this.copy(Cmd.GETSERVERIP, this.cmdBuf, 0, 4);		this.copy(sn, this.cmdBuf, 4, 4);	}}