package com.pl.schedule.protocol.udp.cmd;import java.util.Arrays;import com.pl.schedule.protocol.udp.Cmd;import com.pl.schedule.protocol.udp.Utils;import com.pl.schedule.protocol.udp.model.Auth;public class QueryAuth extends CommCmd {	public Auth getResult() {		return new Auth(Arrays.copyOfRange(this.recvBuff, 4, this.recvBuff.length));	}	public QueryAuth(String ip, byte[] sn, int cardNo) {		super(ip, sn);		this.copy(Cmd.QUERYAUTH, this.cmdBuf, 0, 4);		this.copy(sn, this.cmdBuf, 4, 4);		this.copy(Utils.int2Byte((long) cardNo), this.cmdBuf, 8, 4);	}}