package com.pl.schedule.protocol.udp.cmd;import java.util.Arrays;import com.pl.schedule.protocol.udp.Cmd;import com.pl.schedule.protocol.udp.model.CtrlStatus;public class QueryStatus extends CommCmd {	public CtrlStatus getResult() {		return new CtrlStatus(Arrays.copyOfRange(this.recvBuff, 4, this.recvBuff.length));	}	public QueryStatus(String ip, byte[] sn) {		super(ip, sn);		this.copy(Cmd.STATUS, this.cmdBuf, 0, 0, 4);		this.copy(sn, this.cmdBuf, 0, 4, 4);	}}