package com.pl.schedule.protocol.udp.cmd;import java.util.Arrays;import com.pl.schedule.protocol.udp.Cmd;import com.pl.schedule.protocol.udp.Utils;import com.pl.schedule.protocol.udp.model.DoorSetting;public class SetDoorPara extends CommCmd {	public DoorSetting getResult() {		return new DoorSetting(Arrays.copyOfRange(this.recvBuff, 4, this.recvBuff.length));	}	public SetDoorPara(String ip, byte[] sn, int doorNo, int status, int delay) {		super(ip, sn);		this.copy(Cmd.SETPARA, this.cmdBuf, 0, 4);		this.copy(sn, this.cmdBuf, 4, 4);		this.copy(Utils.int2Byte((long) doorNo), this.cmdBuf, 8, 1);		this.copy(Utils.int2Byte((long) status), this.cmdBuf, 9, 1);		this.copy(Utils.int2Byte((long) delay), this.cmdBuf, 10, 1);	}}